import * as express from 'express';
import {User} from '../models/user';
import {UserCRUD} from '../crud/UserCRUD';
import { AppCRUD } from '../crud/AppCRUD';

export const userRouter = express.Router();

// POST
userRouter.post('/api/signup', (req, res) => {
  const user = new User(req.body);
  UserCRUD.postSignUp(res, user);
});

userRouter.post('/api/signin', (req, res) => {
  UserCRUD.postSignIn(res, req, User);
});

userRouter.post('/api/password-reset', (req, res) => {
  UserCRUD.postPasswordReset(res, req, User);
});

// GET
userRouter.get('/api/user', (req, res) => {
  UserCRUD.getLoggedUser(req, res);
});

userRouter.get('/api/users', (req, res) => {
  const filter = req.query.id?{_id: req.query.id.toString()}:{};
  AppCRUD.get(res, filter, User);
});

// PATCH
userRouter.patch('/api/password-reset', (req, res) => {
  const allowedUpdates = ['password'];
  const actualUpdates = Object.keys(req.body);
  const isValidUpdate =
    actualUpdates.every((update) => allowedUpdates.includes(update));

  if (!isValidUpdate) {
    res.status(400).send({
      error: 'Update is not permitted',
    });
  } else {
    UserCRUD.patchPassword(req, res, User);
  }
});

 userRouter.patch('/api/users', (req, res) => {
  const allowedUpdates = ['name', 'email', 'password', 'description', 'age'];
  const actualUpdates = Object.keys(req.body);
  const isValidUpdate =
    actualUpdates.every((update) => allowedUpdates.includes(update));

  if (!isValidUpdate) {
    res.status(400).send({
      error: 'Update is not permitted',
    });
  } else {
    if (!req.query.id) {
      res.status(400).send({
        error: 'An id must be provided',
      });
    }
    AppCRUD.patch(req, res, User);
  }
});

// DELETE
userRouter.delete('/api/users', (req, res) => {
  if (!req.query.id) {
    res.status(400).send({
      error: 'An id must be provided',
    });
  }
  UserCRUD.delete(req, res, User);
});

import {Document, Schema, model} from 'mongoose';
import validator from 'validator';

export interface UserDocumentInterface extends Document {
  name: string,
  email: string,
  password: string,
  description: string,
  following: number,
  followers: number,
  age: number,
}

export const UserSchema = new Schema<UserDocumentInterface>({
  name: {
    type: String,
    unique: true,
    required: true,
    trim: true,
  },
  email: {
    type: String,
    unique: true,
    required: true,
    trim: true,
    validate: (value: string) => {
      if (!validator.isEmail(value)) {
        throw new Error('Email not valid');
      }
    },
  },
  password: {
    type: String,
    required: true,
    trim: true,
    validate: (value: string) => {
      if (!validator.isStrongPassword(value, { minLength: 8, minLowercase: 1, minUppercase: 1, minNumbers: 1, minSymbols: 0})) {
        throw new Error('Must be a strong password');
      }
    },
  },
  description: {
    type: String,
    default: "",
    unique: false,
    required: false,
    trim: true,
  },
  following: {
    type: Number,
    default: 0,
    required: true,
  },
  followers: {
    type: Number,
    default: 0,
    required: true,
  },
  age: {
    type: Number,
    required: false,
  },
});

export const User = model<UserDocumentInterface>('User', UserSchema);

import * as jwt from 'jsonwebtoken';
import { UserDocumentInterface } from '../models/user';
import { handleErrors } from './assets/handleErrors';
import { sendEmail } from './assets/sendEmail';

export class UserCRUD {

  public static async postSignUp(res: any, element: UserDocumentInterface) {
    try {
      await element.save();
      return res.status(201).send(element);
    } catch (error) {
      handleErrors(error, res);
    }
  }

  public static async postSignIn(res: any, req: any, model: any) {
    try {
      const element = await model.findOne({email:req.body.email, password:req.body.password});

      if (element) {
        jwt.sign({element}, 'secretkey', {'expiresIn': '86400s'}, (err: any, token: any) => {
          if (err) {
            return res.status(500).send({error: err});
          } else {
            return res.status(200).send({user: element, token});
          }
        });
      } else {
        return res.status(404).send({error:'Correo electrÃ³nico o contraseÃ±a incorrectos'});
      }
    } catch (error) {
      return res.status(500).send({error: error});
    }
  }

  public static async postPasswordReset(res: any, req: any, model: any) {
    try {
      const element = await model.findOne({email: req.body.email});

      if (element) {
        jwt.sign({element}, 'secretkey', {'expiresIn': '300s'}, (err: any, token: any) => {
          if (err) {
            return res.status(500).send(err);
          } else {
            sendEmail(res, req, token);
          }
        });
      } else {
        return res.status(404).send({error: 'Correo electrÃ³nico no registrado'});
      }
    } catch (error) {
      return res.status(500).send({error: error});
    }
  }

  public static getLoggedUser(req: any, res: any){
    const bearerHeader =  req.headers['authorization'];

    if (typeof bearerHeader !== 'undefined'){
      const bearerToken = bearerHeader.split(" ")[1];

      jwt.verify(bearerToken, 'secretkey', (err: any, authData: any) => {
        if (err){
          return res.status(403).send({error: 'La sesiÃ³n ha expirado'});
        } else {
          return res.status(200).send(authData);
        }
      });
    } else {
      return res.status(400).send({error: 'Debe proporcionarse un token'});
    }
  }

  public static async patchPassword(req: any, res: any, model: any) {
    const bearerHeader =  req.headers['authorization'];
    if (typeof bearerHeader !== 'undefined') {
      const bearerToken = bearerHeader.split(" ")[1];

      jwt.verify(bearerToken, 'secretkey', async (err: any, authData: any) => {
        if (err) {
          return res.status(403).send({error: 'La sesiÃ³n ha expirado'});
        } else {
          try {
            const element = await model.findByIdAndUpdate(authData.element._id, req.body, {
              new: true,
              runValidators: true,
            });

            if (!element) {
              return res.status(404).send({error: 'Elemento no encontrado'});
            }

            return res.status(200).send(element);
          } catch (err) {
            handleErrors(err, res);
          }
        }
      });
    } else {
      return res.status(400).send({error: 'Debe proporcionarse un token'});
    }
  }

  // DELETE
  public static async delete(req: any, res: any, model: any) {
    const bearerHeader =  req.headers['authorization'];

    if (typeof bearerHeader !== 'undefined'){
      const bearerToken = bearerHeader.split(" ")[1];

      jwt.verify(bearerToken, 'secretkey', async (err: any, authData: any) => {
        if (err){
          return res.status(403).send({error: 'La sesiÃ³n ha expirado'});
        } else {
          try {
            if(authData.element._id !== req.query.id) {
              return res.status(403).send('EliminaciÃ³n no permitida');
            }
            const element = await model.findByIdAndDelete(authData.element._id);

            if (!element) {
              return res.status(404).send();
            }

            return res.status(200).send(element);
          } catch (error) {
            return res.status(400).send();
          }
        }
      });
    } else {
      return res.status(400).send({error: 'Debe proporcionarse un token'});
    }
  }

}

export function handleErrors(err: any, res: any) {
  if(err.name === 'ValidationError') {
    return res.status(400).send({error: "La contraseÃ±a debe contener al menos ocho caracteres, un nÃºmero y una mayÃºscula", fields: 'password'});
  }
  else if(err.code && err.code == 11000) return err = handleDuplicateKeyError(err, res);
  else res.status(500).send({error: 'Error desconocido'});
}

export  function handleDuplicateKeyError(err: any, res: any) {
  const field = Object.keys(err.keyValue);
  const code = 409;
  var campo = field[0];
  if (campo === 'name') {
    campo = 'nombre';
  } else {
    campo = 'correo electrÃ³nico';
  }
  res.status(code).send({error: `Ya existe una cuenta con ese ${campo}.`, fields: field});
}

